import styled from 'styled-components';
export const Container = styled.div`
    height: 100vh;
    width: 100vw;
    display: flex;
    justify-content: center;
    align-items: center;
`;

export const FormContainer = styled.form`
    background-color: black;
    width: 50%;
    height: 30%;
    border-radius: 20px;
    display: flex;
    flex-direction: column;
`;

export const Title = styled.h1`
    color: white;
    text-align: center;
`;

export const InputLabel = styled.label`
    color: white;
    margin-left: 10px;
    margin-right: 10px;
`;

export const Input = styled.input`
    padding: 9px;
    font-size: 16px;
    border-width: 3px;
    border-color: #cccccc;
    background-color: #ffffff;
    color: #000000;
    border-style: solid;
    border-radius: 12px;
    width: 70%;
    margin-left: 10px;
    margin-right: 10px;
`;
